// Définition de l'interface Vehicle
interface Vehicle {
    make: string;
    model: string;
    year: number;
    start(): void;
}

// Implémentation de la classe Car qui suit l'interface Vehicle
class Car implements Vehicle {
    make: string;
    model: string;
    year: number;

    constructor(make: string, model: string, year: number) {
        this.make = make;
        this.model = model;
        this.year = year;
    }

    start(): void {
        console.log("Car engine started");
    }
}

// Création d'une instance de Car et appel de la méthode start
const myCar = new Car("Toyota", "Corolla", 2023);
myCar.start(); // Affiche : "Car engine started"
